import C from './config'
import 'https://smtpjs.com/v3/smtp.js'
import { SMTPClient } from 'emailjs';

const client = new SMTPClient({
	user: C.mail_username,
	password: C.mail_password,
	host: C.mail_host,
	ssl: true,
});

export const Mail=(val)=>{

  client.send(
     	{
	     	text: 'i hope this works',
        to: C.email,
	     	...val
    	},(err, message) => {
	      	 if(err){
		         return err
      		 }
	      	 return 200
     	})
  }
